        if ($this->isColumnModified(JobPeer::PARAMETER)) {
            $modifiedColumns[':p' . $index++]  = '`parameter`';
        }
        if ($this->isColumnModified(JobPeer::FAIL_REASON)) {
            $modifiedColumns[':p' . $index++]  = '`fail_reason`';
